import React, { useState } from "react";
import { BannerSection, BannerSectionTitle } from "../../components";
import { Container, Row, Col } from "react-bootstrap";
import BannerCreatorWrapper from "../../assets/wrappers/BannerCreator/BannerCreatorWrapper";
import BannerPreview from "./BannerPreview";
import BannerControls from "./BannerControls/BannerControls";
import { v4 as uuidv4 } from "uuid";

const addStyleBasedType = (type) => {
  if (type === "heading") {
    return {
      fontSize: "40px",
      fontWeight: "bold",
      color: "#fff",
    };
  }

  if (type === "subheading") {
    return { fontSize: "20px", color: "#fff" };
  }

  return { fontWeight: "100", color: "#fff" };
};

const BannerCreator = () => {
  const [cover, setCover] = useState(null);
  const [colorPicker, setColorPicker] = useState("#d3d3d3");
  const [items, setItems] = useState([]);

  const onChangeColorPicker = (color) => {
    setColorPicker(color.hex);
    setCover(null);
  };

  const uploadCover = (e) => {
    e.preventDefault();
    if (e.target.files[0]) {
      setCover(URL.createObjectURL(e.target.files[0]));
      setColorPicker("#fff");
    }
  };

  const populateBannerText = (text, type) => {
    setItems([
      ...items,
      {
        id: uuidv4(),
        text,
        style: addStyleBasedType(type),
      },
    ]);
  };

  const saveChanges = (itemId, newText) => {
    const newState = items.map((obj) => {
      if (obj.id === itemId) {
        console.log(true);
        return { ...obj, text: newText };
      }

      return obj;
    });

    setItems(newState);
  };

  return (
    <BannerCreatorWrapper>
      <BannerSectionTitle />

      <BannerSection title="A Drive Through Experience"></BannerSection>

      <Container fluid className="mb-5 py-5">
        <Row>
          <Col lg="12" className="mb-5 py-5" style={{ textAlign: "center" }}>
            <h1>Welcome to Our Banner Creator</h1>
            <h5 className="design-font">
              <u>rotate - drag - resize - add your own style</u>
            </h5>
          </Col>
        </Row>

        <Row>
          <Col lg="1"></Col>
          <Col lg="4">
            <BannerControls
              uploadCover={uploadCover}
              onChangeColorPicker={onChangeColorPicker}
              color={colorPicker}
              populateBannerText={populateBannerText}
            />
          </Col>

          <Col lg="6">
            <BannerPreview
              cover={cover}
              color={colorPicker}
              items={items}
              saveChanges={saveChanges}
            />
          </Col>

          <Col lg="1"></Col>
        </Row>
      </Container>
    </BannerCreatorWrapper>
  );
};

export default BannerCreator;
