import React from "react";
import { Card } from "react-bootstrap";
import BlogDescription from "../../../components/BlogDescription";

const Post = ({ project }) => {
  const postProps = () => {
    return {
      backgroundImage: `linear-gradient(rgba(26, 11, 11, 0), rgb(0 0 0 / 82%)), url(${project})`,
    };
  };

  return (
    <Card className="justify-content-end" style={{ ...postProps() }}>
      <BlogDescription
        width="100"
        title={project.title}
        date={project.date}
        category={project.category}
      />
    </Card>
  );
};

export default Post;
