import React from "react";
import { LockFill, UnlockFill, Trash } from "react-bootstrap-icons";

const DraggedItemControls = ({
  removeItem,
  item,
  draggable,
  handleLockDraggedItem,
}) => {
  return (
    <div
      className="draggable-controls"
      style={{
        position: "absolute",
        top: "-50px",
        padding: "10px 8px",
        background: "white",
        borderRadius: "5px",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        cursor: "pointer",
      }}
    >
      <Trash
        className="trash-icon"
        onClick={() => {
          removeItem(item.id);
        }}
      />

      {draggable ? (
        <UnlockFill
          onClick={() => handleLockDraggedItem(false)}
          style={{ marginLeft: "10px" }}
        />
      ) : (
        <LockFill
          onClick={() => handleLockDraggedItem(true)}
          style={{ marginLeft: "10px" }}
        />
      )}
    </div>
  );
};

export default DraggedItemControls;
