import React, { useState, useRef } from "react";
import { Rnd } from "react-rnd";
import {
  DRAGGED_ITEM_NEWSTYLE,
  DRAGGED_ITEM_DEFAULTSTYLE,
} from "../../../utils/helpers/constants";
import ContentEditable from "react-contenteditable";
import useClickOutside from "../../../utils/hooks/useClickOutside";
import { ChatLeftHeart, Trash } from "react-bootstrap-icons";

// https://codesandbox.io/s/4rlw34mnk7?file=/src/index.js
const Draggable = ({ item, saveChanges, removeItem }) => {
  const [dragStart, setDragStart] = useState(false);
  const [draggable, setDraggable] = useState(false);
  const [control, setControl] = useState(false);
  const [style, setStyle] = useState({});
  const text = useRef(item.text);
  const dragRef = useRef();
  const [delta, setDelta] = useState(null);

  useClickOutside(dragRef, () => {
    setDragStart(false);
    setControl(false);
    setDraggable(false);
  });

  const onResize = (e, direction, ref, { height }, position) => {
    setDelta(height);
  };

  const handleChange = (evt) => {
    text.current = evt.target.value;
    saveChanges(item.id, text.current);
  };

  const adjust = (ref) => {
    // console.log(ref.node.style.cssText);
    // const props = ref.path[2].style.cssText.split("; ");
    const props = ref.node.style.cssText.split("; ");
    let obj = {};
    let arr = [];
    props.forEach((prop, index) => {
      const tup = prop.split(": ");
      let props = tup[0].split("-");

      if (props.length === 1) {
        arr.push(props.join(" "));
      } else {
        const a = props[0];
        const b = props[1].charAt(0).toUpperCase() + props[1].slice(1);
        const str = a + b;
        arr.push(str);
      }

      obj[arr[index]] = tup[1].split(";").join("");
    });

    setStyle(obj);
  };

  const onDragStart = (e) => {
    setDragStart(true);
    setControl(true);

    if (e.target.className !== "react-draggable") {
      setDraggable(true);
    }
  };

  const onResizeStart = () => {
    setDragStart(true);
    setControl(false);
  };

  const newStyle = dragStart
    ? { ...DRAGGED_ITEM_NEWSTYLE, border: "2px solid #7000d9" }
    : { ...DRAGGED_ITEM_NEWSTYLE };

  return (
    <>
      <Rnd
        style={newStyle}
        bounds="parent"
        onDragStart={onDragStart}
        onResize={onResize}
        onResizeStart={onResizeStart}
        disableDragging={draggable}
        default={{ ...DRAGGED_ITEM_DEFAULTSTYLE }}
      >
        <div
          style={{
            width: "100%",
            height: "100%",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
          }}
          ref={dragRef}
        >
          {control && (
            <div
              className="draggable-controls"
              style={{
                position: "absolute",
                top: "-50px",
                padding: "10px 8px",
                background: "white",
                borderRadius: "5px",
                display: "flex",
                justifyContent: "center",
                alignItems: "center",
                cursor: "pointer",
              }}
            >
              <Trash
                onClick={() => {
                  removeItem(item.id);
                }}
              />
              <ChatLeftHeart style={{ marginLeft: "10px" }} />
            </div>
          )}
          <ContentEditable
            style={item.style}
            // id={item.id}
            html={text.current}
            onChange={handleChange}
            className="content-editable"
          />
        </div>
      </Rnd>
    </>
  );
};

export default Draggable;
