import React, { useState, useRef } from "react";
import { Rnd } from "react-rnd";
import ContentEditable from "react-contenteditable";
import useClickOutside from "../../utils/hooks/useClickOutside";
import { BoundingBoxCircles } from "react-bootstrap-icons";

let style = {
  display: "flex",
  alignItems: "center",
  justifyContent: "center",
  border: "2px solid transparent",
};

// https://codesandbox.io/s/4rlw34mnk7?file=/src/index.js
const DraggableComponent = ({ item, saveChanges, removeItem }) => {
  const [dragStart, setDragStart] = useState(false);
  const text = useRef(item.text);
  const dragRef = useRef();
  const [delta, setDelta] = useState(null);

  useClickOutside(dragRef, () => setDragStart(false));

  const onResize = (e, direction, ref, { height }, position) => {
    setDelta(height);
  };

  const handleChange = (evt) => {
    text.current = evt.target.value;
    saveChanges(item.id, text.current);
  };

  const onDragStart = (e) => {
    setDragStart(true);
    // console.log("drag start");
  };

  const onDragStop = (e) => {
    // console.log("drag stop");
  };

  const newStyle = dragStart
    ? { ...style, border: "2px solid #7000d9" }
    : { ...style };

  return (
    <>
      <Rnd
        style={newStyle}
        bounds="parent"
        onDragStart={onDragStart}
        onResize={onResize}
        onDragStop={onDragStop}
        default={{
          x: 0,
          y: 0,
          width: "auto",
          height: "auto",
          maxWidth: "300px",
          maxHeight: "100px",
        }}
      >
        <div
          style={{
            width: "100%",
            height: "100%",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
          }}
          ref={dragRef}
        >
          <div>
            <BoundingBoxCircles />
          </div>
          <ContentEditable
            style={item.style}
            id={item.id}
            onDoubleClick={removeItem}
            html={text.current}
            onChange={handleChange}
            className="content-editable"
          />
        </div>
      </Rnd>
    </>
  );
};

export default DraggableComponent;
