import React, { useState, useEffect } from "react";
import { Container, Col, Row } from "react-bootstrap";
import {
  PageSectionTitle,
  PageSectionBanner,
  ButtonPrimary,
} from "../../../components";
import bannerCover from "../../../assets/images/img5.jpeg";
import { useParams } from "react-router-dom";
import { GET_PROJECT_ROUTE } from "../../../utils/services/apis";
import axios from "axios";
import { Arrow90degRight } from "react-bootstrap-icons";
import ProjectComments from "./Comments/Comments";
import { fetchSingleProject } from "../../../utils/services/services";

const RenderProject = ({ project }) => {
  return (
    <Col className="mb-5" key={project} lg="4">
      <img
        src={project}
        className="img-fluid w-100 mb-3"
        style={{ objectFit: "cover", height: "500px" }}
      />
      <p>
        Lorem ipsum dolor sit amet consectetur adipisicing elit. Rerum ea earum
        aspernatur explicabo voluptates, libero cupiditate harum velit nulla,
        autem labore in doloremque magnam deserunt porro. Voluptas vel minima
        autem!
      </p>
      <ButtonPrimary>
        Preview <Arrow90degRight />
      </ButtonPrimary>
    </Col>
  );
};

const RenderProjects = ({ project }) => {
  const renderProjects = () => {
    return ["desktop", "tablet", "mobile"].map((p) => {
      return <RenderProject key={p} project={project[p]} />;
    });
  };
  return <>{project._id ? renderProjects() : "loading"}</>;
};

const SingleProject = () => {
  const { projectId } = useParams();
  const [project, setProject] = useState({});

  const fetchProject = async () => {
    try {
      const {
        data: { project },
      } = await axios.get(GET_PROJECT_ROUTE(projectId));

      setProject(project);
    } catch (error) {
      console.log(error.response.data.msg);
    }
  };

  const renderCategory = () => {
    return (
      <>
        {project._id && (
          <>
            Category:{" "}
            <i>
              <u> {project.category}</u>
            </i>
          </>
        )}
      </>
    );
  };

  useEffect(() => {
    fetchProject();
  }, []);

  return (
    <>
      <PageSectionTitle
        subtitle="explore the posibilities!"
        titleBold="Realm Of Ux's"
        titleNormal=" ultimate guide for creating stunning social media banners!"
      />

      <PageSectionBanner
        title="A Drive Through Experience"
        cover={bannerCover}
      />

      <Container className="my-5">
        <Row>
          <Col lg="12">
            <h1 className="mb-0">{project && project.name}</h1>
            <p className="mt-0">{renderCategory()}</p>
          </Col>

          <RenderProjects project={project} />
        </Row>

        <Row>
          <Col lg="12">
            <ProjectComments projectId={projectId} />
          </Col>
        </Row>
      </Container>
    </>
  );
};

export default SingleProject;
