export const trimUserName = (name) => {
  const firstName = name.split(" ")[0];
  const secondName = name.split(" ")[1].split("")[0];
  return `Hello, ${firstName} ${secondName}.`;
};

export const getCoverSizes = () => {
  return {
    fbPost: {
      width: 1000,
      height: 800,
    },
    fbCover: {
      width: 1200,
      height: 1000,
    },
    instaPost: {
      width: 800,
      height: 600,
    },
    instaCover: {
      width: 500,
      height: 400,
    },
  };
};
