import React, { useState, useEffect } from "react";
import { Card } from "react-bootstrap";
import BlogDescription from "../../../components/BlogDescription";

const Project = ({ project }) => {
  const [blogPath, setBlobPath] = useState("");

  const converToBlobFromBase64 = () => {
    fetch(project.desktop)
      .then((res) => res.blob())
      .then((res) => {
        const blob = window.URL.createObjectURL(res);
        setBlobPath(blob);
      })
      .catch((err) => console.log(err));
  };

  useEffect(() => {
    converToBlobFromBase64();
  });

  const postProps = () => {
    return {
      backgroundImage: `linear-gradient(rgba(26, 11, 11, 0), rgb(91 47 124 / 42%)), url(${blogPath})`,
    };
  };

  return (
    <Card className="justify-content-end" style={{ ...postProps() }}>
      <BlogDescription
        width="100"
        title={project.name}
        date="21th September"
        category={project.category}
      />
    </Card>
  );
};

export default Project;
