import React, { useState, useRef } from "react";
import { Rnd } from "react-rnd";
import ContentEditable from "react-contenteditable";
import useClickOutside from "../../utils/hooks/useClickOutside";

let style = {
  display: "flex",
  alignItems: "center",
  justifyContent: "center",
  border: "2px solid transparent",
  //   padding: "1em",
};

// https://codesandbox.io/s/4rlw34mnk7?file=/src/index.js
const DraggableComponent = ({ item, saveChanges }) => {
  const [dragStart, setDragStart] = useState(false);
  const text = useRef(item.text);
  const dragRef = useRef();
  const [delta, setDelta] = useState(null);

  useClickOutside(dragRef, () => setDragStart(false));

  const onResize = (e, direction, ref, { width }, position) => {
    setDelta(width);
  };

  const handleChange = (evt) => {
    text.current = evt.target.value;
    saveChanges(item.id, text.current);
  };

  const onDragStart = (e) => {
    setDragStart(true);
  };

  const newStyle = dragStart
    ? { ...style, border: "2px solid #7000d9" }
    : { ...style };

  return (
    <Rnd
      style={newStyle}
      bounds="parent"
      onDragStart={onDragStart}
      onResize={onResize}
      default={{
        x: 0,
        y: 0,
        width: "auto",
        height: "auto",
        maxWidth: "300px",
        maxHeight: "100px",
      }}
    >
      <div
        style={{
          width: "100%",
          height: "100%",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
        }}
        ref={dragRef}
      >
        <ContentEditable
          //   style={item.style}
          style={{ fontSize: `${40 + delta}px` }}
          html={text.current}
          id={item.id}
          onChange={handleChange}
          className="content-editable"
        />
      </div>
    </Rnd>
  );
};

export default DraggableComponent;
