import React, { useState } from "react";
import { Button, Form } from "react-bootstrap";
import FloatingLabel from "react-bootstrap/FloatingLabel";
import { ButtonPrimary, ButtonSecondary } from "../../../../components";
import { ADD_COMENT_ROUTE } from "../../../../utils/services/apis";
import axios from "axios";
import { useUserContext } from "../../../../utils/contexts/user/userContext";

const AddCommentForm = ({ projectId }) => {
  const [comment, setComment] = useState("");
  const { user } = useUserContext();

  const onHandleChange = (e) => {
    setComment(e.target.value);
  };

  const handleOnSubmit = async (e) => {
    e.preventDefault();
    try {
      const userComment = {
        comment,
        userEmail: user.email,
        projectId,
      };

      const response = await axios.post(ADD_COMENT_ROUTE, userComment);
      setComment("");
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div className="mb-5">
      <h3>Add A Comment</h3>
      <p>You must be logged in in order to add a comment!</p>
      <Form onSubmit={handleOnSubmit}>
        <FloatingLabel controlId="floatingTextarea2" label="Comments">
          <Form.Control
            as="textarea"
            onChange={onHandleChange}
            placeholder="Leave a comment here"
            style={{ height: "100px" }}
          />
        </FloatingLabel>

        <ButtonSecondary type="submit" className="mt-3">
          Add Comment
        </ButtonSecondary>
      </Form>
    </div>
  );
};

export default AddCommentForm;
