import React, { useState, useRef } from "react";
import { Rnd } from "react-rnd";
import {
  DRAGGED_ITEM_NEWSTYLE,
  DRAGGED_ITEM_DEFAULTSTYLE,
} from "../../../utils/helpers/constants";
import ContentEditable from "react-contenteditable";
import useClickOutside from "../../../utils/hooks/useClickOutside";
import DraggedItemControls from "./DraggedItemControls";
import "./styles.scss";

// https://codesandbox.io/s/4rlw34mnk7?file=/src/index.js
const Draggable = ({ item, saveChanges, removeItem }) => {
  const [draggable, setDraggable] = useState(false);
  const [showControls, setShowControls] = useState(false);
  const [lock, setLock] = useState(false);
  const text = useRef(item.text);
  const dragRef = useRef();
  const [delta, setDelta] = useState(null);

  useClickOutside(dragRef, () => {
    setShowControls(false);
    // setDraggable(false);
  });

  const handleOnResize = (e, direction, ref, { height }, position) => {
    setDelta(height);
  };

  const handleChange = (evt) => {
    text.current = evt.target.value;
    saveChanges(item.id, text.current);
  };

  //DRAG
  const handleOnDragStart = (e) => {
    setShowControls(true);
    // if (e.target.className !== "react-draggable") {
    //   setDraggable(true);
    // }
  };

  const handleOnDrag = () => {
    // setShowControls(false);
  };

  const handleOnDragStop = () => {
    setShowControls(true);
  };

  //RESIZE
  const handleOnResizeStart = () => {
    setDraggable(true);
    setShowControls(true);
  };

  const handleOnResizeStop = () => {
    setShowControls(true);
  };

  const handleLockDraggedItem = (cb) => {
    setDraggable(cb);
    setLock(cb);
  };

  const newStyle = showControls
    ? { ...DRAGGED_ITEM_NEWSTYLE, border: "2px solid #7000d9" }
    : { ...DRAGGED_ITEM_NEWSTYLE };

  return (
    <>
      <Rnd
        style={{ ...newStyle }}
        bounds="parent"
        onDragStart={handleOnDragStart}
        onResizeStop={handleOnResizeStop}
        onDrag={handleOnDrag}
        onResize={handleOnResize}
        onDragStop={handleOnDragStop}
        onResizeStart={handleOnResizeStart}
        disableDragging={draggable}
        default={{ ...DRAGGED_ITEM_DEFAULTSTYLE }}
      >
        <div className="draggable-wrapper" ref={dragRef}>
          {showControls && (
            <DraggedItemControls
              draggable={draggable}
              removeItem={removeItem}
              item={item}
              handleLockDraggedItem={handleLockDraggedItem}
            />
          )}
          <ContentEditable
            style={item.style}
            html={text.current}
            onChange={handleChange}
            className="content-editable"
          />
        </div>
      </Rnd>
    </>
  );
};

export default Draggable;
