import React from "react";
import DraggableComponent from "../Draggable";

const coverStyle = (cover) => {
  return {
    backgroundImage: `url(${cover})`,
    backgroundPosition: "center center",
    backgroundSize: "cover",
  };
};

const colorStyle = {
  backgroundColor: color,
};

const BannerPreview = ({ cover, color, items, saveChanges, bannerSize }) => {
  const coverOrColor = cover ? coverStyle : colorStyle(cover);

  const defaultStyle = {
    width: `${bannerSize.width}px`,
    height: `${bannerSize.height}px`,
    position: "relative",
    ...coverOrColor,
  };

  return (
    <div>
      {/*  Create another component */}
      <div className="mb-3">
        <h5 className="font-weight-bold">Settings:</h5>
        <p className="my-0">
          Current Banner Type: <u>Facebbok Cover</u>
        </p>
        <p className="my-0">
          Current Banner Type: <u>Facebbok Cover</u>
        </p>
      </div>
      <div style={{ ...defaultStyle }}>
        {items.map((item) => {
          return (
            <DraggableComponent
              key={item.id}
              item={item}
              saveChanges={saveChanges}
            />
          );
        })}
      </div>
    </div>
  );
};

export default BannerPreview;
