export const trimUserName = (name) => {
  const firstName = name.split(" ")[0];
  const secondName = name.split(" ")[1].split("")[0];
  return `Hello, ${firstName} ${secondName}.`;
};

export const getBannerSizes = () => {
  return {
    fbCover: {
      width: 1100,
      height: 9000,
    },
    fbPost: {
      width: 1000,
      height: 800,
    },
    instaCover: {
      width: 500,
      height: 400,
    },
    instaPost: {
      width: 800,
      height: 600,
    },
  };
};

export const addStyleBasedType = () => {
  return {
    heading: {
      fontSize: "40px",
      fontWeight: "bold",
      color: "#fff",
    },

    subheading: {
      fontSize: "20px",
      color: "#fff",
    },

    paragraph: {
      fontWeight: "100",
      color: "#fff",
    },
  };
};
