import { Action } from "history";
import React, { useState } from "react";
import { useEffect } from "react";
import Button from "react-bootstrap/Button";
import Toast from "react-bootstrap/Toast";
import ToastContainer from "react-bootstrap/ToastContainer";

const ToastNotification = ({ status, message, displayAlert }) => {
  const [show, setShow] = useState(displayAlert);

  const statusStyle = {
    width: "10px",
    height: "10px",
    backgroundColor: status === "success" ? "#0e930e" : "#d60d0d",
  };

  useEffect(() => {
    setShow(displayAlert);
  }, [displayAlert]);

  return (
    <>
      <ToastContainer position="bottom-end">
        <Toast
          onClose={() => setShow(false)}
          show={show}
          delay={3000}
          autohide
          bg="dark"
        >
          <Toast.Header closeButton={true}>
            <div style={statusStyle} className="rounded me-2 icon-status"></div>
            <strong className="me-auto">
              {status === "success" ? "Success" : "Fail"}
            </strong>
            <small>11 mins ago</small>
          </Toast.Header>
          <Toast.Body style={{ color: "#e3d7d7" }}>{message}</Toast.Body>
        </Toast>
      </ToastContainer>

      {/* <Button onClick={() => setShow(true)}>Show Toast</Button> */}
    </>
  );
};

export default ToastNotification;
