import React from "react";
import Home from "./pages/Home/Home";
import BannerCreator from "./pages/BannerCreator/BannerCreator";
import { Navigation, Footer } from "./components";
import LoginRegister from "./pages/LoginRegister/LoginRegister";
import { ThemeProvider } from "styled-components";
import { TranslateProvider } from "./utils/contexts/translate/translateContext";
import { Routes, Route } from "react-router-dom";
import { UserProvider } from "./utils/contexts/user/userContext";
import Projects from "./pages/Projects";
import ProjectForm from "./pages/Projects/ProjectForm";
import SingleProject from "./pages/Home/Projects/SingleProject";

const theme = {
  mobile: "(min-width: 320px) and (max-width:479px)",
};

//https://9elements.github.io/fancy-border-radius/

function App() {
  return (
    <ThemeProvider theme={theme}>
      <TranslateProvider>
        <UserProvider>
          <div className="App">
            <Navigation />
            <Routes>
              <Route path="/" element={<Home />}>
                <Route path="projects" element={<Projects />} />
              </Route>
              <Route
                index
                path="/projects/:projectId"
                element={<SingleProject />}
              />

              <Route index path="/login" element={<LoginRegister />} />
              <Route index path="/banner-creator" element={<BannerCreator />} />
              <Route
                index
                path="/projects/add-project"
                element={<ProjectForm />}
              />
            </Routes>
            <Footer />
          </div>
        </UserProvider>
      </TranslateProvider>
    </ThemeProvider>
  );
}

export default App;
