import React, { useState, useRef } from "react";
import { Rnd } from "react-rnd";
import ContentEditable from "react-contenteditable";
import useClickOutside from "../../utils/hooks/useClickOutside";
import {
  BoundingBoxCircles,
  ChatLeftHeart,
  Trash,
} from "react-bootstrap-icons";

let style = {
  display: "flex",
  alignItems: "center",
  justifyContent: "center",
  border: "2px solid transparent",
};

// https://codesandbox.io/s/4rlw34mnk7?file=/src/index.js
const DraggableComponent = ({ item, saveChanges, removeItem }) => {
  const [dragStart, setDragStart] = useState(false);
  const [control, setControl] = useState(false);
  const text = useRef(item.text);
  const dragRef = useRef();
  const [delta, setDelta] = useState(null);

  useClickOutside(dragRef, () => setDragStart(false));

  const onResize = (e, direction, ref, { height }, position) => {
    setDelta(height);
  };

  const handleChange = (evt) => {
    text.current = evt.target.value;
    saveChanges(item.id, text.current);
  };

  const onDragStart = (e) => {
    setDragStart(true);
    setControl(true);
  };

  const onDrag = () => {
    setControl(true);
  };

  const onDragStop = (e) => {
    // console.log("drag stop");
  };

  const onResizeStart = () => {
    setDragStart(false);
  };

  const newStyle = dragStart
    ? { ...style, border: "2px solid #7000d9" }
    : { ...style };

  return (
    <>
      <Rnd
        style={newStyle}
        bounds="parent"
        onDragStart={onDragStart}
        onResize={onResize}
        onDragStop={onDragStop}
        onResizeStart={onResizeStart}
        default={{
          x: 0,
          y: 0,
          width: "auto",
          height: "auto",
          maxWidth: "300px",
          //   maxHeight: "100px",
        }}
      >
        <div
          style={{
            width: "100%",
            height: "100%",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
          }}
          ref={dragRef}
        >
          {control && (
            <div
              style={{
                position: "absolute",
                top: "-50px",
                padding: "10px 8px",
                background: "white",
                borderRadius: "5px",
                display: "flex",
                justifyContent: "center",
                alignItems: "center",
                cursor: "pointer",
              }}
            >
              <Trash onClick={() => removeItem(item.id)} />
              <ChatLeftHeart style={{ marginLeft: "10px" }} />
            </div>
          )}
          <ContentEditable
            style={item.style}
            // id={item.id}
            html={text.current}
            onChange={handleChange}
            className="content-editable"
          />
        </div>
      </Rnd>
    </>
  );
};

export default DraggableComponent;
