import React from "react";
import { Card } from "react-bootstrap";
import BlogDescription from "../../../components/BlogDescription";

const Project = ({ project }) => {
  let responses = [];

  data.projects.forEach(({ desktop }) => {
    let response = fetch(desktop)
      .then((res) => res.blob())
      .then((res) => window.URL.createObjectURL(res));

    responses.push(response);
  });

  Promise.all(responses).then((res) => setProjects([...projects, ...res]));
  const postProps = () => {
    return {
      backgroundImage: `linear-gradient(rgba(26, 11, 11, 0), rgb(91 47 124 / 42%)), url(${project.desktop})`,
    };
  };

  return (
    <Card className="justify-content-end" style={{ ...postProps() }}>
      <BlogDescription
        width="100"
        title={project.name}
        date="21th September"
        category={project.category}
      />
    </Card>
  );
};

export default Project;
