export const trimUserName = (name) => {
  const firstName = name.split(" ")[0];
  const secondName = name.split(" ")[1].split("")[0];
  return `Hello, ${firstName} ${secondName}.`;
};

export const getBannerSizes = () => {
  return {
    fbPost: {
      width: 1000,
      height: 800,
    },
    fbCover: {
      width: 1200,
      height: 1000,
    },
    instaPost: {
      width: 800,
      height: 600,
    },
    instaCover: {
      width: 500,
      height: 400,
    },
  };
};

export const addStyleBasedType = () => {
  if (type === "heading") {
    return {
      fontSize: "40px",
      fontWeight: "bold",
      color: "#fff",
    };
  }

  if (type === "subheading") {
    return { fontSize: "20px", color: "#fff" };
  }

  return { fontWeight: "100", color: "#fff" };
};
