import React from "react";
import DraggableComponent from "../Draggable";

const BannerPreview = ({ cover, color, items, saveChanges, bannerSize }) => {
  const coverStyle = {
    backgroundImage: `url(${cover})`,
    backgroundPosition: "center center",
    backgroundSize: "cover",
  };

  const colorStyle = {
    backgroundColor: color,
  };

  const coverOrColor = cover ? coverStyle : colorStyle;

  const defaultStyle = {
    width: "100%",
    height: "700px",
    position: "relative",
    ...coverOrColor,
  };

  return (
    <div style={{ ...defaultStyle }}>
      {items.map((item) => {
        return (
          <DraggableComponent
            key={item.id}
            item={item}
            saveChanges={saveChanges}
          />
        );
      })}
    </div>
  );
};

export default BannerPreview;
