import React from "react";
import { CLone, Trash } from "react-bootstrap-icons";

const DraggedItemControls = ({
  removeItem,
  item,
  draggable,
  handleLockDraggedItem,
}) => {
  return (
    <div
      className="draggable-controls"
      style={{
        position: "absolute",
        top: "-50px",
        padding: "10px 8px",
        background: "white",
        borderRadius: "5px",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        cursor: "pointer",
      }}
    >
      <Trash
        className="trash-icon"
        onClick={() => {
          removeItem(item.id);
        }}
      />

      <p
        className="lock-me my-0"
        onClick={(e) => handleLockDraggedItem(e, !draggable)}
      >
        {draggable ? (
          <LockFill style={{ marginLeft: "10px" }} className="lock" />
        ) : (
          <UnlockFill
            onClick={() => handleLockDraggedItem(true)}
            style={{ marginLeft: "10px" }}
            className="unlock"
          />
        )}
      </p>
    </div>
  );
};

export default DraggedItemControls;
