import React from "react";

const RenderComments = ({ comments, deleteComment }) => {
  console.log(comments);
  return (
    <>
      <h2>Comments</h2>
      {comments.map((comm) => (
        <div key={comm._id}>
          <p className="mb-0">
            user: <i>{comm.userEmail}</i>
          </p>
          <p>{comm.comment}</p>
          <button onClick={() => console.log(comm.userEmail)}>
            Delete comment
          </button>
          <hr />
        </div>
      ))}
    </>
  );
};
export default RenderComments;
