import React from "react";
import DraggableComponent from "../Draggable";
import { getBannerType } from "../../../utils/helpers";
import BannerSettings from "../BannerPreview/BannerSettings";

const coverStyle = (cover) => {
  return {
    backgroundImage: `url(${cover})`,
    backgroundPosition: "center center",
    backgroundSize: "cover",
  };
};

const colorStyle = (color) => {
  return { backgroundColor: color };
};

const BannerPreview = ({
  cover,
  color,
  items,
  saveChanges,
  bannerSize,
  removeItem,
}) => {
  const coverOrColor = cover ? coverStyle(cover) : colorStyle(color);
  const defaultStyle = {
    width: `${bannerSize.width}px`,
    height: `${bannerSize.height}px`,
    position: "relative",
    ...coverOrColor,
  };

  const { title } = getBannerType(bannerSize.width);

  return (
    <div>
      <BannerSettings bannerName={title} />

      <div style={{ ...defaultStyle }}>
        {items.map((item) => {
          return (
            <DraggableComponent
              removeItem={removeItem}
              key={item.id}
              item={item}
              saveChanges={saveChanges}
            />
          );
        })}
      </div>
    </div>
  );
};

export default BannerPreview;
