import React, { useState, useEffect } from "react";
import { Card } from "react-bootstrap";
import BlogDescription from "../../../components/BlogDescription";
import { useConvertBase64ToBlob } from "../../../utils/hooks";

const Project = ({ project }) => {
  const blogPath = useConvertBase64ToBlob(project);

  const postProps = () => {
    return {
      backgroundImage: `linear-gradient(rgba(26, 11, 11, 0), rgb(91 47 124 / 42%)), url(${blogPath})`,
    };
  };

  return (
    <Card className="justify-content-end" style={{ ...postProps() }}>
      <BlogDescription
        width="100"
        title={project.name}
        date="21th September"
        category={project.category}
      />
    </Card>
  );
};

export default Project;
