import React from "react";
import BannerSectionWrapper from "../assets/wrappers/BannerSectionWrapper";
import cover from "../assets/images/img18.jpeg";
import { Container, Row, Col } from "react-bootstrap";

const PageSectionBanner = ({ title }) => {
  return (
    <BannerSectionWrapper cover={cover}>
      <Container fluid>
        <Row className="justify-content-center align-items-center">
          <Col lg="6" className="banner-section-container">
            <h1>{title}</h1>
          </Col>
        </Row>
      </Container>
    </BannerSectionWrapper>
  );
};

export default PageSectionBanner;
