import { TYPES } from "./constants";

export const trimUserName = (name) => {
  const firstName = name.split(" ")[0];
  const secondName = name.split(" ")[1].split("")[0];
  return `Hello, ${firstName} ${secondName}.`;
};

/*
    {
        title: "Facebook Cover",
        id: "fbCover",
    },
    {
        title: "Facebook Post",
        id: "fbPost",
    },
    {
        title: "Instagram Cover",
        id: "instaCover",
    },
    {
        title: "Instagram Post",
        id: "instaPost",
    },
*/
export const getBannerSizes = () => {
  return {
    fbCover: {
      width: 1000,
      height: 800,
    },
    fbPost: {
      width: 900,
      height: 600,
    },
    instaCover: {
      width: 500,
      height: 400,
    },
    instaPost: {
      width: 800,
      height: 600,
    },
  };
};

// based on the banner size I want to get the banner name
export const getBannerType = (width) => {
  const sizes = getBannerSizes();
  let actualType = "";

  for (let size in sizes) {
    if (size.width === 800) {
      actualType = sizes["instaCover"];
    }
  }

  console.log(actualType);
};

export const addStyleBasedType = () => {
  return {
    heading: {
      fontSize: "40px",
      fontWeight: "bold",
      color: "#fff",
    },

    subheading: {
      fontSize: "20px",
      color: "#fff",
    },

    paragraph: {
      fontWeight: "100",
      color: "#fff",
    },
  };
};
