import React from "react";
import { Form } from "react-bootstrap";
import FloatingLabel from "react-bootstrap/FloatingLabel";
import { ButtonSecondary } from "../../../../components";

const FormComment = ({ hadleChange, addComment, comment }) => {
  const disableBtn = !comment.comment ? "enabled" : "disabled";
  return (
    <div className="mb-5">
      <div className="mb-5">
        <h3 className="mb-2">Add A Comment</h3>
        <p>You must be logged in in order to add a comment!</p>
      </div>
      <Form onSubmit={addComment}>
        <FloatingLabel
          controlId="floatingTextarea2"
          label="Place your comment here..."
        >
          <Form.Control
            as="textarea"
            onChange={hadleChange}
            placeholder="Leave a comment here"
            style={{ height: "100px" }}
            value={comment}
          />
        </FloatingLabel>

        <ButtonSecondary type="submit" {...disableBtn} className="mt-3">
          Add Comment
        </ButtonSecondary>
      </Form>
    </div>
  );
};

export default FormComment;
