import React, { useEffect } from "react";
import BlogPostsWrapper from "../../../assets/wrappers/Home/BlogPostsWrapper";
import Post from "./Post";
import { Container, Col, Row } from "react-bootstrap";
import { ArrowRight } from "react-bootstrap-icons";
import {
  ButtonPrimary,
  TitleSection,
  TitleSectionDesign,
} from "../../../components";
import { useTranslateContext } from "../../../utils/contexts/translate/translateContext";

const BlogPosts = () => {
  const {
    homepage: {
      blog: {
        titleSection: { title, subtitle },
        subtitleSection: { description },
        blogData,
      },
    },
  } = useTranslateContext();

  const [projects, setProjects] = useState([]);

  return (
    <BlogPostsWrapper white>
      <Container className="blog-posts">
        <Row className="justify-content-center">
          <Col lg="12">
            <TitleSectionDesign
              textAlign="right"
              centerMargin="right"
              description={description}
            />
            <TitleSection
              title={title}
              subtitle={subtitle}
              centerMargin="center"
            />
          </Col>
        </Row>

        <Row className="justify-content-center">
          {blogData.map((post, idx) => {
            return (
              <Col key={idx} lg={idx === 1 || idx === 4 ? "4" : "4"}>
                <Post post={post} />
              </Col>
            );
          })}

          <Col lg="12" className="text-center mt-5">
            <ButtonPrimary href="https://google.com" target="_blank">
              Discover More <ArrowRight />
            </ButtonPrimary>
          </Col>
        </Row>
      </Container>
    </BlogPostsWrapper>
  );
};

export default BlogPosts;
