import styled from "styled-components";

const BannerCreatorWrapper = styled.div`
  [type="file"] {
    /* Style the color of the message that says 'No file chosen' */
    color: #878787;
  }

 .rect {
    cursor: move;
    user-select: none;
  }

  .content-editable {
    &:focus-visible {
        outline:none;
    }
  }


  [type="file"]::-webkit-file-upload-button {
    background: #ed1c1b;
    border: 2px solid #ed1c1b;
    border-radius: 4px;
    color: #fff;
    cursor: pointer;
    font-size: 12px;
    outline: none;
    padding: 5px 15px;
    text-transform: uppercase;
    transition: all 0.5s ease;
  }

  [type="file"]::-webkit-file-upload-button:hover {
    background: #fff;
    border: 2px solid #535353;
    color: #000;
  }

  .banner-control {
    min-height: 150px;
    display: flex;
    flex-direction: column;
    justify-content: center;
    padding: 1rem;
    border-radius:5px;
    margin-bottom:1rem;

    &-cover {
        background-color: lightgray;
    }

    &-text {
        background-color: aliceblue;

        button.heading {
            background: #0d1216;
            margin-bottom:8px;
            padding:8px 16px;
            border-radius:5px;
            color:var(--text-color-white);
            text-align:left;
            border:none;

            &:hover {
                background-color:#18191b;
            }
        }

        &-heading {
            font-size:28px;
            font-weight:bold;
        }

        &-subheading {
            font-size:20px;
        }

        &-paragraph {
            font-size:16px;
            font-weight:100;
        }
    }

    &-background {
        background-color: ghostwhite;

        button {
            background: #4d3939;
            border:2px solid #4d3939;
            border-radius: 4px;
            color: #fff;
            cursor: pointer;
            font-size: 12px;
            outline: none;
            padding: 5px 20px;
            text-transform: uppercase;
            transition: all 0.5s ease;
            width:fit-content;
            display:block;
            flex-shrink:0;

            &:hover {
                background: #fff;
                border: 2px solid #535353;
                color: #000;
            }
        }
    }
  }

  .banner-cover-control
`;

export default BannerCreatorWrapper;
