import React from "react";
import { ChevronDoubleUp, Trash } from "react-bootstrap-icons";

const DraggedItemControls = ({
  removeItem,
  item,
  draggable,
  handleLockDraggedItem,
}) => {
  return (
    <div
      className="draggable-controls"
      style={{
        position: "absolute",
        top: "-50px",
        padding: "10px 8px",
        background: "white",
        borderRadius: "5px",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        cursor: "pointer",
      }}
    >
      <Trash
        className="trash-icon"
        onClick={() => {
          removeItem(item.id);
        }}
      />
      <ChevronDoubleUp className="duplicate" />
    </div>
  );
};

export default DraggedItemControls;
